import * as S from './quests-catalog.styled';
import { AppRoute, Genres } from 'const.js';
import { useSelector } from 'react-redux';
import { getCurrentGenre, getQuestsByGenre } from 'store/selectors.js';
import GenreItem from './genre-item.jsx';

const QuestsCatalog = () => {
  const currentGenre = useSelector(getCurrentGenre);
  const quests = useSelector(getQuestsByGenre);

  return (
  <>
    <S.Tabs>
    {Object.values(Genres).map(genre => <GenreItem key={genre} genre={genre} isActiveGenre={currentGenre === genre} />)}
    </S.Tabs>

    <S.QuestsList>


      <S.QuestItem>
        <S.QuestItemLink to={AppRoute.QuestPage}>
          <S.Quest>
            <S.QuestImage
              src="img/preview-house-in-the-woods.jpg"
              width="344"
              height="232"
              alt="квест Хижина в лесу"
            />

            <S.QuestContent>
              <S.QuestTitle>Хижина в лесу</S.QuestTitle>

              <S.QuestFeatures>
                <S.QuestFeatureItem>
                  <IconPerson />
                  4–7 чел
                </S.QuestFeatureItem>
                <S.QuestFeatureItem>
                  <IconPuzzle />
                  средний
                </S.QuestFeatureItem>
              </S.QuestFeatures>
            </S.QuestContent>
          </S.Quest>
        </S.QuestItemLink>
      </S.QuestItem>
    </S.QuestsList>
  </>
  )
};

export default QuestsCatalog;
